+**********************************************/
+/*----------------------------------------*/
+/*  01. THEME DEFAULT CSS START
+/*----------------------------------------*/
+@import url("https://fonts.googleapis.com/css2?family=Jost:wght@300;400;500;600;700display=swap");
+
+:root {
+    /**
+  @font family declaration
+  */
+    --tp-ff-body: 'Jost', sans-serif;
+    --tp-ff-fontawesome: "Font Awesome 5 Pro";
+    /**
+  @color declaration
