@charset "UTF-8";
/*-----------------------------------------------------------------------------------

    Theme Name: Ninico - Minimal eCommerce HTML Template
    Author: Theme Pure
    Support: basictheme@gmail.com
    Description: Template Name  â€“ HTML5 Template
    Version: 1.0

-----------------------------------------------------------------------------------

/************ TABLE OF CONTENTS ***************

    01. THEME DEFAULT CSS
    02. HEADER CSS
    03. MEAN MENU CSS
	04. SLIDER CSS START
	05. CATEGORY CSS START
	06. PRODUCT CSS START
	07. SHOP CSS START
	08. BANNER CSS START
	09. MARQUE CSS START
	10. PORTFOLIO CSS START
	11. BLOG CSS START
	12. CTA CSS START
	13. SERVICES CSS START
	14. BRAND CSS START
	15. PLATINAM-PRODUCT CSS START
	16. FEATURES CSS START
	17. TESTIMONIAL CSS START
	18. SELECTPRODUCT CSS START
	19. PRODUCT-DETAILS CSS START
	20. CART CSS START
	21. LOCATION CSS START
	22. SIGN CSS START
	23. COMING-SOON CSS START
	24. FOOTER CSS START


**********************************************/
/*----------------------------------------*/
/*  01. THEME DEFAULT CSS START
/*----------------------------------------*/
@import url("https://fonts.googleapis.com/css2?family=Jost:wght@300;400;500;600;700display=swap");

:root {
    /**
  @font family declaration
  */
    --tp-ff-body: 'Jost', sans-serif;
    --tp-ff-fontawesome: "Font Awesome 5 Pro";
    /**
  @color declaration
  */
    --tp-common-white: #ffffff;
    --tp-common-black: #000;
    --tp-common-yellow: #ffcd00;
    --tp-heading-primary: #000;
    --tp-grey-1: #a5a5a6;
    --tp-grey-2: #f3f4f7;
    --tp-grey-3: #c8c8c8;
    --tp-grey-4: #e6dfd6;
    --tp-grey-5: #fff3f6;
    --tp-grey-6: #cfcfcf;
    --tp-grey-7: #d3d3d3;
    --tp-grey-8: #f6f6f6;
    --tp-grey-9: #999999;
    --tp-text-body: #040404;
    --tp-text-primary: #d51243;
    --tp-text-secondary: #777777;
    --tp-text-tertiary: #adadad;
    --tp-text-4: #a0a0a0;
    --tp-text-5: #999999;
    --tp-theme-1: #3D6CE7;
    --tp-theme-2: #f3eee7;
    --tp-theme-3: #f7f7f7;
    --tp-theme-4: #77977b;
    --tp-theme-5: #8e8b74;
    --tp-theme-6: #f8f8f8;
    --tp-theme-7: #ededed;
    --tp-theme-8: #f4f1ec;
    --tp-theme-9: #83b735;
    --tp-theme-10: #f3f4f7;
    --tp-theme-11: #f3f7f7;
    --tp-theme-12: #f0e2dd;
    --tp-theme-13: #f7efec;
    --tp-theme-14: #f58448;
    --tp-border-1: #e7e7e7;
    --tp-border-2: #f6f6f6;
    --tp-border-3: #ecf0ef;
    --tp-border-4: #e0e0e0;
    --tp-border-5: #282828;
    --tp-border-6: #e4e4e4;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

/*---------------------------------
	typography css start
---------------------------------*/
body {
    font-family: var(--tp-ff-body);
    font-size: 18px;
    font-weight: 400;
    color: var(--tp-text-body);
    line-height: 26px;
    overflow-x: hidden !important;
}

a {
    text-decoration: none;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: var(--tp-ff-body);
    color: var(--tp-header-1);
    margin-top: 0px;
    font-weight: var(--tp-fw-sbold);
    line-height: 1.2;
    -webkit-transition: all 0.3s ease-out 0s;
    -moz-transition: all 0.3s ease-out 0s;
    -ms-transition: all 0.3s ease-out 0s;
    -o-transition: all 0.3s ease-out 0s;
    transition: all 0.3s ease-out 0s;
}

h1 {
    font-size: 40px;
}

h2 {
    font-size: 32px;
}

h3 {
    font-size: 28px;
}

h4 {
    font-size: 24px;
}

h5 {
    font-size: 20px;
}

h6 {
    font-size: 16px;
}

ul {
    margin: 0px;
    padding: 0px;
}

p {
    font-family: var(--tp-ff-body);
    font-size: 18px;
    font-weight: 400;
    color: var(--tp-text-body);
    margin-bottom: 15px;
    line-height: 26px;
}

a,
.btn,
button,
p,
i,
input,
select,
textarea,
li,
img,
.transition-3,
h1,
h2,
h3,
h4,
h5,
h6 {
    -webkit-transition: all 0.3s ease-out 0s;
    -moz-transition: all 0.3s ease-out 0s;
    -ms-transition: all 0.3s ease-out 0s;
    -o-transition: all 0.3s ease-out 0s;
    transition: all 0.3s ease-out 0s;
}

a:focus,
.button:focus {
    text-decoration: none;
    outline: none;
}

a:focus,
a:hover {
    color: inherit;
    text-decoration: none;
}

a {
    text-decoration: none;
}

a,
button {
    color: inherit;
    outline: none;
    border: none;
    background: transparent;
}

button:hover {
    cursor: pointer;
}

button:focus {
    outline: 0;
}

.uppercase {
    text-transform: uppercase;
}

.capitalize {
    text-transform: capitalize;
}

input {
    outline: none;
}

input[type=color] {
    appearance: none;
    -moz-appearance: none;
    -webkit-appearance: none;
    background: none;
    border: 0;
    cursor: pointer;
    height: 100%;
    width: 100%;
    padding: 0;
    border-radius: 50%;
}

*::-moz-selection {
    background: var(--tp-common-black);
    color: var(--tp-common-white);
    text-shadow: none;
}

::-moz-selection {
    background: var(--tp-common-black);
    color: var(--tp-common-white);
    text-shadow: none;
}

::selection {
    background: var(--tp-text-primary);
    color: var(--tp-common-white);
    text-shadow: none;
}

*::-moz-placeholder {
    color: var(--tp-common-black);
    font-size: var(--tp-fz-body);
    opacity: 1;
}

*::placeholder {
    color: var(--tp-common-black);
    font-size: var(--tp-fz-body);
    opacity: 1;
}

/*---------------------------------
    common classes css start
---------------------------------*/
.w-img img {
    width: 100%;
}

.m-img img {
    max-width: 100%;
}

.fix {
    overflow: hidden;
}

.clear {
    clear: both;
}

.z-index-1 {
    z-index: 1;
}

.z-index-11 {
    z-index: 11;
}

.overflow-y-visible {
    overflow-x: hidden;
    overflow-y: visible;
}

.p-relative {
    position: relative;
}

.p-absolute {
    position: absolute;
}

.include-bg {
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
}

.gx-6 {
    --bs-gutter-x: 80px;
}

@media only screen and (min-width: 1200px) and (max-width: 1399px),
only screen and (min-width: 992px) and (max-width: 1199px) {
    .gx-6 {
        --bs-gutter-x: 30px;
    }
}

@media only screen and (min-width: 768px) and (max-width: 991px),
only screen and (min-width: 576px) and (max-width: 767px) {
    .gx-6 {
        --bs-gutter-x: 30px;
    }
}

.f-right {
    float: right;
}

/* scrollUp */
.scroll-top {
    width: 40px;
    height: 40px;
    line-height: 40px;
    position: fixed;
    bottom: -10%;
    right: 50px;
    font-size: 16px;
    border-radius: 6px;
    z-index: 99;
    color: var(--tp-common-white);
    text-align: center;
    cursor: pointer;
    background: var(--tp-text-primary);
    transition: 1s ease;
    border: none;
}

@media (max-width: 767px) {
    .scroll-top {
        right: 30px;
        width: 40px;
        height: 40px;
        line-height: 40px;
    }
}

.scroll-top.open {
    bottom: 30px;
}

.scroll-top::after {
    position: absolute;
    z-index: -1;
    content: "";
    top: 100%;
    left: 5%;
    height: 10px;
    width: 90%;
    opacity: 1;
    background: radial-gradient(ellipse at center, rgba(0, 0, 0, 0.25) 0%, rgba(0, 0, 0, 0) 80%);
}

.scroll-top:hover {
    background: var(--tp-text-body);
}

/*----------------------------------------*/
/*  Spacing
/*----------------------------------------*/
/*----------------------------------------*/
/*  00. BUTTON CSS START
/*----------------------------------------*/
.tp-btn {
    display: inline-block;
    font-size: 14px;
    font-weight: 600;
    padding: 17px 33px;
    border-radius: 6px;
    color: var(--tp-text-body);
    background: var(--tp-common-white);
    line-height: 1.2;
}

.tp-btn:hover {
    color: var(--tp-common-white);
    background-color: var(--tp-text-primary);
}

.tp-btn:hover i {
    animation: iconarrow 0.4s linear;
}

.tp-btn i {
    color: var(--tp-text-2);
    margin-left: 10px;
    text-transform: uppercase;
}

@media (max-width: 767px) {
    .tp-btn i {
        margin-left: 2px;
    }
}

.tpsecondary-btn {
    display: inline-block;
    font-size: 14px;
    font-weight: 600;
    padding: 17px 33px;
    border-radius: 6px;
    background: var(--tp-text-primary);
    color: var(--tp-common-white);
    line-height: 1.2;
    position: relative;
}

.tpsecondary-btn::before {
    position: absolute;
    content: "";
    background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0.2) 100%);
    left: -10%;
    top: 0;
    height: 100%;
    width: 10%;
}

.tpsecondary-btn:hover::before {
    animation: lightwhite 0.8s;
}

.tpsecondary-btn:hover i {
    animation: iconarrow 0.4s linear;
}

.tpsecondary-btn i {
    color: var(--tp-text-2);
    margin-left: 10px;
    text-transform: uppercase;
}

.tpcart-btn,
.tpcheck-btn {
    border: 2px solid var(--tp-text-primary);
    display: block;
    justify-content: center;
    align-items: center;
    font-size: 13px;
    min-height: 45px;
    text-transform: uppercase;
    background: var(--tp-text-primary);
    color: var(--tp-common-white);
    border-radius: 30px;
    padding: 10px 30px;
    text-align: center;
    line-height: 1.5;
    padding: 14px;
    font-weight: 600;
}

.tpcart-btn:hover,
.tpcheck-btn:hover {
    background-color: #b50833;
    color: var(--tp-common-white);
    border: 2px solid #b50833;
}

.tpcart-btn {
    background-color: transparent;
    color: var(--tp-text-primary);
}

.tpcart-btn:hover {
    background-color: #b50833;
    color: var(--tp-common-white);
    border: 2px solid #b50833;
}

.tp-color-btn {
    background-color: var(--tp-text-primary);
    color: var(--tp-common-white);
}

/* 01.  ========= pulse effect animation ========== */
@-webkit-keyframes pulse {
    0% {
        -webkit-box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.4);
    }

    70% {
        -webkit-box-shadow: 0 0 0 45px rgba(255, 255, 255, 0);
    }

    100% {
        -webkit-box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);
    }
}

@keyframes pulse {
    0% {
        -moz-box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.4);
        box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.4);
    }

    70% {
        -moz-box-shadow: 0 0 0 45px rgba(255, 255, 255, 0);
        box-shadow: 0 0 0 45px rgba(255, 255, 255, 0);
    }

    100% {
        -moz-box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);
        box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);
    }
}

/* 02. ========= btn arrow animation ========== */
@keyframes iconarrow {
    49% {
        transform: translateX(30%);
    }

    50% {
        opacity: 0;
        transform: translateX(-30%);
    }

    51% {
        opacity: 1;
    }
}

/* 03. ========= btn arrow animation ========== */
@-webkit-keyframes shine {
    100% {
        left: 100%;
    }
}

@keyframes shine {
    100% {
        left: 100%;
    }
}

/* 04. ========= firefly ========== */
@keyframes firefly {
    0% {
        opacity: 1;
    }

    50% {
        opacity: 0;
    }

    100% {
        opacity: 1;
        color: white;
    }
}

/* 05. ========= firefly ========== */
@keyframes hotdeal {
    0% {
        opacity: 1;
    }

    50% {
        opacity: 0.6;
    }

    75% {
        opacity: 0.8;
    }

    100% {
        opacity: 1.5;
    }
}

/* 06. ========= firefly ========== */
@keyframes lightwhite {
    100% {
        left: 100%;
    }
}

/* 07. ========= lighttitle ========== */
@keyframes lighttitle {
    100% {
        left: 100%;
    }
}

/* 08. ========= banneranimation ========== */
.banner-animation {
    position: relative;
    overflow: hidden;
}

.banner-animation::before {
    background: rgba(255, 255, 255, 0.3);
    bottom: 0;
    content: "";
    left: 50%;
    position: absolute;
    right: 50%;
    pointer-events: none;
    top: 0;
    opacity: 1;
    width: 0;
    transition: all 900ms linear;
}

.banner-animation:hover::before {
    left: 0;
    right: 0;
    opacity: 0;
    width: auto;
    transition: all 900ms linear;
}

/* 09. ========= banneranimation ========== */
@-webkit-keyframes rotate {
    from {
        transform: rotate(0deg);
    }

    to {
        transform: rotate(360deg);
    }
}

@keyframes rotate {
    from {
        transform: rotate(0deg);
    }

    to {
        transform: rotate(360deg);
    }
}

/* 02. ========= video ripple animation ========== */
@keyframes tp-pulse-2 {
    0% {
        box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.4);
    }

    70% {
        box-shadow: 0 0 0 45px rgba(255, 255, 255, 0);
    }

    100% {
        box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);
    }
}

/* 02. ========= section animation ========== */
@keyframes section-animation {
    0% {
        width: 0;
    }

    15% {
        width: 100%;
    }

    85% {
        opacity: 1;
    }

    90% {
        width: 100%;
        opacity: 0;
    }

    to {
        width: 0;
        opacity: 0;
    }
}

/*--- preloader ---*/
.dark #preloader {
    background-color: #232323;
}

#preloader {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #f7f7f7;
    z-index: 999999;
}

.preloader {
    width: 50px;
    height: 50px;
    display: inline-block;
    padding: 0px;
    text-align: left;
    box-sizing: border-box;
    position: absolute;
    top: 50%;
    left: 50%;
    margin-left: -25px;
    margin-top: -25px;
}

.preloader span {
    position: absolute;
    display: inline-block;
    width: 50px;
    height: 50px;
    border-radius: 100%;
    background: #D51243;
    -webkit-animation: preloader 1.3s linear infinite;
    animation: preloader 1.3s linear infinite;
}

.preloader span:last-child {
    animation-delay: -0.8s;
    -webkit-animation-delay: -0.8s;
}

@keyframes preloader {
    0% {
        transform: scale(0, 0);
        opacity: 0.5;
    }

    100% {
        transform: scale(1, 1);
        opacity: 0;
    }
}

@-webkit-keyframes preloader {
    0% {
        -webkit-transform: scale(0, 0);
        opacity: 0.5;
    }

    100% {
        -webkit-transform: scale(1, 1);
        opacity: 0;
    }
}

